import RSA from '@/libs/crypto';
import http from '@/libs/http';
import { getToken } from '@/libs/util';
export default {
  inject: ['reload'],
  name: 'tables_page',
  data: function data() {
    return {
      target: '',
      min: 1,
      max: 65535,
      scanner: 'nmap',
      rate: 1000
    };
  },
  created: function created() {
    this.$nextTick(this.getParams());
