            _this.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
            });

            break;

          case 'Z1002':
            _this.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
            });

            break;

          case 'Z1004':
            _this.$Notice.error({
              title: '请求失败',
              desc: '认证失败,请稍后再次尝试'
            });

            break;

          default:
            break;
        }
      });
    }
  }
};
