import AES from '@/libs/crypto';
import http from '@/libs/http';
import { getToken } from '@/libs/util';
import MarkdownItVue from 'markdown-it-vue';
export default {
  inject: ['reload'],
  components: {
    MarkdownItVue: MarkdownItVue
  },
  data: function data() {
    return {
      item: {},
      token: getToken(),
      username: ''
    };
  },
  created: function created() {
    this.$nextTick(this.getParams());
  },
  methods: {
    getParams: function getParams() {
      this.item = JSON.parse(JSON.stringify(this.$route.query.params));
    },
    edit: function edit(params) {
      this.$router.push({
        name: '修改笔记',
        query: {
          params: this.item
        }
      });
    },
    trash: function trash(item) {
      var _this = this;

      var flag = {
        'type': 'trash',
        'data': '1'
      };
      var data = {
        'notename': AES.Encrypt(item.notename),
        'flag': flag,
        'token': this.token.trim()
      };
      data = JSON.stringify(data);
      var req_params = {
        'data': AES.Encrypt(data)
      };
      http.post('/api/setflag', req_params).then(function (res) {
        res.data = eval('(' + res.data + ')');

        switch (res.data.code) {
          case 'Z1000':
            setTimeout(function () {
              _this.$router.push({
                path: '/components/notelist'
              });
            }, 3000);
            break;

          case 'Z1001':
            _this.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
            });

            break;

          case 'Z1002':
            _this.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
            });

            break;

          case 'Z1004':
            _this.$Notice.error({
              title: '请求失败',
              desc: '认证失败,请稍后再次尝试'
            });

            break;

          default:
            break;
        }
      });
    }
  }
};
