import RSA from '@/libs/crypto';
import http from '@/libs/http';
import { getToken } from '@/libs/util';
export default {
  inject: ['reload'],
  name: 'tables_page',
  data: function data() {
    var _this = this;

    return {
      target: '',
      token: getToken(),
      page: {
        pageNum: 1,
        pageSize: 5,
        count: 0
      },
      columns: [{
        title: '目标',
        key: 'target',
        sortable: true,
        resizable: true,
        width: 240
      }, {
        title: '描述',
        key: 'description',
        sortable: true,
        resizable: true,
        width: 240
      }, {
        title: 'IP:PORT',
        key: 'ip_port',
        resizable: true,
        width: 240
      }, {
        title: '漏洞名称',
        key: 'vulner_name',
        resizable: true,
        width: 240
      }, {
        title: '漏洞描述',
        key: 'vulner_descrip',
        resizable: true,
        width: 250
      }, {
        title: '操作',
        key: 'action',
        width: 300,
        resizable: true,
        align: 'center',
