            _this2.$Notice.error({
              title: '获取数据失败',
              desc: '系统发生异常,请稍后再次尝试'
            });

            break;

          case 'Z1002':
            _this2.$Notice.error({
              title: '获取数据失败',
              desc: '系统发生异常,请稍后再次尝试'
            });

            break;

