              desc: '系统发生异常,请稍后再次尝试'
            });

            break;

          case 'Z1002':
            _this3.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
            });

            break;

          case 'Z1004':
            _this3.$Notice.error({
              title: '请求失败',
              desc: '认证失败,请稍后再次尝试'
            });

            break;

          default:
            break;
        }
      });
    },
    handlePage: function handlePage(pageNum) {
      this.page.pageNum = pageNum;
      this.getTableData();
    },
    handlePageSize: function handlePageSize(pageSize) {
      this.page.pageSize = pageSize;
      this.getTableData();
    }
  },
  mounted: function mounted() {
    this.getTableData();
  }
};
