import axios from 'axios';
import http from '@/libs/http';
import AES from '@/libs/crypto';
import { getToken } from '@/libs/util';
export default {
  inject: ['reload'],
  name: 'MarkdownEditor',
  data: function data() {
    return {
      notename: '',
      content: '',
      flag: '',
      token: getToken(),
      publish_flag: '0',
      img_file: {}
    };
  },
  methods: {
    handlnotename: function handlnotename() {
      var _this = this;

      if (this.notename == "") {
        this.$Notice.error({
          title: '笔记名字不能为空',
          desc: '请输入笔记名字 '
        });
      } else {
        var data = {
          'type': 'notename',
          'token': this.token,
          'data': AES.Encrypt(this.notename)
        };
        data = JSON.stringify(data);
        var params = {
          'data': AES.Encrypt(data)
        };
        http.post('/api/query', params).then(function (res) {
          res.data = eval('(' + res.data + ')');

          switch (res.data.code) {
            case 'Z1001':
              _this.$Notice.error({
                title: '系统异常',
                desc: '系统发生异常,请稍后再次尝试'
              });

              break;

            case 'Z1002':
              _this.$Notice.error({
                title: '系统异常',
                desc: '系统发生异常,请稍后再次尝试'
              });

              break;

            case 'Z1004':
              _this.$Notice.error({
                title: '认证失败',
                desc: '认证失败,请稍后再次尝试'
              });

              break;

            case 'Z10010':
              _this.flag = '1';

              _this.$Notice.error({
                title: '笔记已存在',
                desc: '笔记名字已存在,请重新输入笔记名字'
              });

              break;

            default:
              break;
          }
        });
      }
    },
    change: function change(status) {
      if (status) {
        this.publish_flag = '1';
      } else {
        this.publish_flag = '0';
      }
    },
    handleSubmit: function handleSubmit() {
      var _this2 = this;

      if (this.notename == "") {
        this.$Notice.error({
          title: '笔记名字不能为空',
          desc: '请输入笔记名字 '
        });
      } else {
        if (this.flag == "1") {
          this.$Notice.error({
            title: '笔记已存在',
            desc: '笔记名字已存在,请重新输入笔记名字'
          });
        } else {
          if (this.content == "") {
            this.$Notice.error({
              title: '笔记内容不能为空',
              desc: '请输入笔记内容'
            });
          } else {
            var data = {
              'token': this.token,
              'notename': AES.Encrypt(this.notename),
              'content': AES.Encrypt(this.$refs.md.d_render),
              'publish_flag': this.publish_flag,
              'trash_flag': '0' //console.log(this.$refs.md.d_render)
              //console.log(this.$refs.md.d_value)

            };
            data = JSON.stringify(data);
            var params = {
              'data': AES.Encrypt(data)
            };
            http.post('/api/save', params).then(function (res) {
              res.data = eval('(' + res.data + ')');

              switch (res.data.code) {
                case 'Z1000':
                  _this2.$Notice.success({
                    title: '保存成功',
                    desc: '笔记保存成功,请到笔记列表查看'
                  });

                  setTimeout(function () {
                    _this2.$router.push({
                      path: '/components/notelist'
                    });
                  }, 1000);
                  break;

                case 'Z1001':
                  _this2.$Notice.error({
                    title: '系统异常',
                    desc: '系统发生异常,请稍后再次尝试'
                  });

                  break;

                case 'Z1002':
                  _this2.$Notice.error({
                    title: '系统异常',
                    desc: '系统发生异常,请稍后再次尝试'
                  });

                  break;

                case 'Z1004':
                  _this2.$Notice.error({
                    title: '认证失败',
                    desc: '认证失败,请稍后再次尝试'
                  });

                  break;

                case 'Z10010':
                  _this2.flag = '1';

                  _this2.$Notice.error({
                    title: '笔记已存在',
                    desc: '笔记名字已存在,请重新输入笔记名字'
                  });

                  break;

                default:
                  break;
              }
            });
          }
        }
      }
    },
    $imgAdd: function $imgAdd(pos, $file) {
      var _this3 = this;

      // 第一步.将图片上传到服务器.
      var instance = axios.create({
        withCredentials: true
      });
      var formdata = new FormData();
      formdata.append('file', $file);
      formdata.append('token', this.token);
      this.img_file[pos] = $file;
      instance({
        url: '/api/upload',
        method: 'post',
        data: formdata,
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(function (res) {
        res.data = eval('(' + res.data + ')');

        switch (res.data.code) {
          case 'Z10010':
            _this3.$Notice.success({
              title: '上传成功',
              desc: res.data.message
            });

            _this3.$refs.md.$img2Url(pos, res.data.data);

            break;

          case 'Z1001':
            _this3.$Notice.error({
              title: '系统异常',
              desc: '系统发生异常,请稍后再次尝试'
            });

            break;

          case 'Z1004':
            _this3.$Notice.error({
              title: '认证失败',
              desc: '认证失败,请稍后再次尝试'
            });

            break;

          case 'Z10011':
            _this3.$Notice.error({
              title: '上传异常',
              desc: '上传失败,请稍候重试!'
            });

            break;

          case 'Z10012':
            _this3.$Notice.error({
              title: '上传异常',
              desc: '上传文件名为空,请添加文件名'
            });

            break;

          case 'Z10013':
            _this3.$Notice.error({
              title: '上传异常',
              desc: '上传文件格式不正确,请稍候重试'
            });

            break;

          default:
            break;
        } // 第二步.将返回的url替换到文本原位置![...](0) -> ![...](url)

        /**
        * $vm 指为mavonEditor实例，可以通过如下两种方式获取
        * 1. 通过引入对象获取: `import {mavonEditor} from ...` 等方式引入后，`$vm`为`mavonEditor`
        * 2. 通过$refs获取: html声明ref : `<mavon-editor ref=md ></mavon-editor>，`$vm`为 `this.$refs.md`
        */

      });
    },
    $imgDel: function $imgDel(pos) {
      delete this.img_file[pos];
    }
  }
};
