import {isemail, isusername, ischecknum, ispassword} from '@/libs/validate'
import RSA  from '@/libs/crypto'
import http  from '@/libs/http'
import {getToken } from '@/libs/util'
export default {
  name: 'NewtargetForm',
  props: {
    targetRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', max:20}
        ]
      }
    },
    descriptionRules: {
      type: Array,
      default: () => {
        return [
          { required: false, trigger: 'blur', max: 20}
        ]
      }
    }
  },
  data () {
    return {
      capta: '',
      form: {
        target: '',
        description: ''
      }
    }
  },
  computed: {
    rules () {
      return {
        target: this.targetRules,
        description: this.descriptionRules
      }
    }
  },
  methods: {
    handleSubmit () {
     this.$refs.NewtargetForm.validate( (valid) => {
        if (valid) {
            this.$emit('on-success-valid', {
              target: this.form.target,
              description: this.form.description,
              token: getToken()
            })
        }
      })
    }
  }
}
