import http from '@/libs/http'
import AES from '@/libs/crypto'
import store from '../../store'
import {getToken } from '@/libs/util'
export default {
  inject: ['reload'],
  name: 'MarkdownEditor',
  data () {
    return {
      notename: '',
      content: '',
      flag : '',
      token: getToken(),
      publish_flag: ''
    }
  },
  methods: {
    handlnotename () {
      if (this.notename == ""){
        this.$Notice.error({
                  title: '笔记名字不能为空',
                  desc: '请输入笔记名字 '
          })
      }
      else{
        let data = {
            'type': 'notename', 
            'token': this.token,
            'data': this.notename
        }
        data = JSON.stringify(data)
        let params = {'data': AES.Encrypt(data)}
        http.post('/api/query', params).then((res) => {
            res.data = eval('(' + res.data + ')')
            switch(res.data.code ){
                case 'Z1001':
                this.$Notice.error({
                  title: '系统异常',
                  desc: '系统发生异常,请稍后再次尝试'
                })
                break
                case 'Z1002':
                this.$Notice.error({
                  title: '系统异常',
                  desc: '系统发生异常,请稍后再次尝试'
                })
                break
                case 'Z10010':
                this.flag = '1'
                this.$Notice.error({
                  title: '笔记已存在',
                  desc: '笔记名字已存在,请重新输入笔记名字'
                })
                break
                default:
                break
            }
        })
      }
    },
    change (status) {
      if (status){
        this.publish_flag = '1'
      }
      else{
        this.publish_flag = '0'
      }
    },
    handleSubmit () {
      this.$emit('on-success-valid', {
        notename: this.notename,
        content: this.content,
        flag: this.flag,
        publish_flag: this.publish_flag,
        token: this.token
      })
    },
    $imgAdd(pos, $file){
            // 第一步.将图片上传到服务器.
          var formdata = new FormData();
          formdata.append('image', $file);
          axios({
              url: '127.0.0.1:5000/upload',
              method: 'post',
              data: formdata,
              headers: { 'Content-Type': 'multipart/form-data' },
          }).then((url) => {
              // 第二步.将返回的url替换到文本原位置![...](0) -> ![...](url)
              // $vm.$img2Url 详情见本页末尾
              $vm.$img2Url(pos, url);
          })
    },
    $imgDel(pos, $file){
            // 第一步.将图片上传到服务器.
          var formdata = new FormData();
          formdata.append('image', $file);
          axios({
              url: '127.0.0.1:5000/upload',
              method: 'post',
              data: formdata,
              headers: { 'Content-Type': 'multipart/form-data' },
          }).then((url) => {
              // 第二步.将返回的url替换到文本原位置![...](0) -> ![...](url)
              // $vm.$img2Url 详情见本页末尾
              $vm.$img2Url(pos, url);
          })
    }
  },
  mounted () {
  }
}
