import {isEmail, isWscnEmail, isUsername, ischeckNum, isPassword} from '../../libs/validate.js'
import {Encrypt}  from '../../libs/crypto'
export default {
  name: 'RegisterForm',
  props: {
    userNameRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: isUsername }
        ]
      }
    },
    eMailRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: isEmail }
        ]
      }
    },
    checkNumRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: ischeckNum }
        ]
      }
    },
    passwordRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: isPassword }
        ]
      }
    },
    passwordConfirmRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: isPassword }
        ]
      }
    }
  },
  data () {
    return {
      sendmail_result: false,
      capta: '',
      form: {
        userName: '',
        eMail: '',
        checkNum: '',
        password: '',
        passwordConfirm: ''
      }
    }
  },
  computed: {
    rules () {
      return {
        userName: this.userNameRules,
        eMail: this.eMailRules,
        checkNum: this.checkNumRules,
        password: this.passwordRules,
        passwordConfirm: this.passwordConfirmRules
      }
    }
  },
  methods: {
    getcheckNum () {
        if (!isWscnEmail(this.form.email)) {
            alert('请输入正确的合法邮箱以来获取注册码')
        }
        else {
           let params = new URLSearchParams()
           params.append('email', this.form.email)
           this.axios.post('/getcheckNum', params).then((res) => {
           //console.log(res)
           //console.log(res.data)
           if (res.data == "0") {
              alert('发送邮件失败')
              setTimeout(function () {
              }.bind(this), 1000)
            }
           else {
              alert('发送邮件成功')
              this.capta = res.data
              //console.log(this.capta)
              this.sendmail_result = true
              setTimeout(function () {
              }.bind(this), 1000)
            }
            })
        }
    },
    handleSubmit () {
      this.$refs.RegisterForm.validate((valid) => {
        if (valid) {
            if (this.sendmail_result == 'flase') {
                alert('请先获取注册码！')
            }
            else {
                if (this.form.checkNum != this.capta) {
                    alert('注册码输入错误,请重新输入！')
                }
                else {
                    if (this.form.password != this.passwordConfirm) {
                        alert('二次密码输入不一致，请重新输入密码！')
                    }
                    else {
                        let params = new URLSearchParams()
                        params.append('username', Encrypt(this.form.username))
                        params.append('email', Encrypt(this.form.email))
                        params.append('password', Encrypt(this.form.password))
                        this.axios.post('/register', params).then((res) => {
                        if (res.data == "1") {
                            this.$router.push({
                                path: '/login'
                            })
                            this.new_username = ''
                            this.new_password = ''
                            setTimeout(function () {
                            }.bind(this), 1000)
                        }
                        else if(res.data == "-1"){
                            alert("注册失败！")
                        }
                        else {
                            alert("系统错误！")
                            setTimeout(function () {
                            }.bind(this), 1000)
                        }
                        })
                    }

                }
            }
        }
        else {
          //console.log('error submit!!')
          return false
        }

       })
    },
    ToLogin () {
      this.$router.push({
        path: '/login'
      })
    }
  }  
}
