import http from '@/libs/http'
import AES from '@/libs/crypto'
import MarkdownEditor from '_c/markdown'
export default {
  name: 'markdown_page',
  components: {
    MarkdownEditor
  },
  data () {
    return {
      content: ''
    }
  },
  methods: {
    handleSubmit ({ notename, content, flag, publish_flag, token }) {
      if (notename == ""){
        this.$Notice.error({
          title: '笔记名字不能为空',
          desc: '请输入笔记名字 '
          })
      }
      else{
        if (flag == "1"){
          this.$Notice.error({
            title: '笔记已存在',
            desc: '笔记名字已存在,请重新输入笔记名字'
          })
        }
        else{
          if (content == ""){
            this.$Notice.error({
                    title: '笔记内容不能为空',
                    desc: '请输入笔记内容'
            })
          }
          else{
            let data = {
              'token': token,
              'notename': notename,
              'content': content,
              'publish_flag': publish_flag,
              'trash_flag': '1'
            }
            data = JSON.stringify(data)
            let params = {'data': AES.Encrypt(data)}
            http.post('/api/save', params).then((res) => {
                res.data = eval('(' + res.data + ')')
                switch(res.data.code ){
                    case 'Z1000':
                    this.$Notice.success({
                      title: '保存成功',
                      desc: '笔记保存成功,请到笔记列表查看'
                    })
                    setTimeout(() => {
                      this.$router.push({
                      path: '/home'
                      })
              },1000)
              break
                    break
                    case 'Z1001':
                    this.$Notice.error({
                      title: '系统异常',
                      desc: '系统发生异常,请稍后再次尝试'
                    })
                    break
                    case 'Z1002':
                    this.$Notice.error({
                      title: '系统异常',
                      desc: '系统发生异常,请稍后再次尝试'
                    })
                    break
                    case 'Z10010':
                    this.flag = '1'
                    this.$Notice.error({
                      title: '笔记已存在',
                      desc: '笔记名字已存在,请重新输入笔记名字'
                    })
                    break
                    default:
                    break
                }
            })
          }
        }
      }
    },
  },

}
