import {isemail, isusername, ischecknum, ispassword} from '@/libs/validate'
import AES  from '@/libs/crypto'
import http  from '@/libs/http'
import {getToken } from '@/libs/util'
export default {
  name: 'ChangepasswordForm',
  props: {
    oldpasswordRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: ispassword }
        ]
      }
    },
    newpasswordRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: ispassword }
        ]
      }
    },
    newpasswordconfirmRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: ispassword }
        ]
      }
    }
  },
  data () {
    return {
      capta: '',
      form: {
        oldpassword: '',
        newpassword: '',
        newpasswordconfirm: ''
      }
    }
  },
  computed: {
    rules () {
      return {
        oldpassword: this.oldpasswordRules,
        newpassword: this.newpasswordRules,
        newpasswordconfirm: this.newpasswordconfirmRules
      }
    }
  },
  methods: {
    getchecknum () {
      if (this.form.email== ''){
        this.$refs.ChangepasswordForm.validateField('email', (valid) => {
        })
      }
      else{
        let data = {
          'type': 'email', 
          'data': this.form.email
        }
        data = JSON.stringify(data)
        let params = {'data': AES.Encrypt(data)}
        http.post('/api/getchecknum', params).then((res) => {
          res.data = eval('(' + res.data + ')')
          switch(res.data.code ){
            case'Z1000':
            this.$Notice.success({
                title: '发送邮件成功',
                desc: '请打开邮件查收验证码 '
            })
            this.capta = res.data.data.capta
            break
            case 'Z1001':
            this.$Notice.error({
                title: '发送邮件失败',
                desc: '系统发生异常,请稍后再次尝试'
            })
            break
            case 'Z1002':
            this.$Notice.error({
                title: '发送邮件失败',
                desc: '系统发生异常,请稍后再次尝试'
            })
            break
          }
        })
      }
    },
    handleSubmit () {
     this.$refs.ChangepasswordForm.validate( (valid) => {
        if (valid) {
          if (this.form.newpassword != this.form.newpasswordconfirm){
            this.$Notice.error({
                title: '二次密码输入不一样',
                desc: '二次密码输入不一样,请重新输入!'
            })
          }
          else{
            this.$emit('on-success-valid', {
              oldpassword: this.form.oldpassword,
              newpassword: this.form.newpassword,
              token: getToken()
            })
          }
        }
      })
    }
  }  
}
