import AES  from '@/libs/crypto'
import http  from '@/libs/http'
import {getToken } from '@/libs/util'
export default {
  inject: ['reload'],
  name: 'tables_page',
  data () {
    return {
      token: getToken(),
      page: {
          pageNum: 1,
          pageSize: 5,
          count: 0
      },
      columns: [
        { 
          title: '笔记名字',
          key: 'notename', 
          sortable: true ,
          resizable: true,
          width: 300
        },
        { 
          title: '创建时间', 
          key: 'time' ,
          resizable: true,
          width: 300
          },
        {
          title: '操作',
          key: 'action',
          resizable: true,
          width: 300,
          align: 'center',
          render: (h, params) => {
            return h('div', [
                h('Button', {
                    props: {
                        type: 'primary',
                        size: 'small'
                    },
                    style: {
                        marginRight: '0px'
                    },
                    on: {
                        click: () => {
                            this.show(params)
                        }
                    }
                }, '查看'),
                h('Button', {
                    props: {
                        type: 'primary',
                        size: 'small'
                    },
                    style: {
                        marginRight: '0px'
                    },
                    on: {
                        click: () => {
                            this.restore(params)
                        }
                    }
                }, '恢复'),
                h('Button', {
                    props: {
                        type: 'error',
                        size: 'small'
                    },
                    on: {
                        click: () => {
                            this.delete(params)
                        }
                    }
                }, '彻底删除')
            ]);
        }
      }
      ],
      tableData: []
    }
  },
  methods: {
    getTableData () {
      let flag = {
        'type': 'trash',
        'data': '1'
      }
      let data = {
        'pagenum': this.page.pageNum,
        'pagesize': this.page.pageSize,
        'flag': flag,
        'token': this.token.trim()
      }
      data = JSON.stringify(data)
      let params = {'data': AES.Encrypt(data)}
      http.post('/api/notelist', params).then((res) => {
        res.data = eval('(' + res.data + ')')
        switch(res.data.code ){
          case'Z1000':
          if (res.data.data !== ""){
            this.tableData = res.data.data
          }
          this.page.count = res.data.total
          break
          case 'Z1001':
          this.$Notice.error({
              title: '获取数据失败',
              desc: '系统发生异常,请稍后再次尝试'
          })
          break
          case 'Z1002':
          this.$Notice.error({
              title: '获取数据失败',
              desc: '系统发生异常,请稍后再次尝试'
          })
          break
          case 'Z1004':
          this.$Notice.error({
              title: '获取数据失败',
              desc: '认证失败,请稍后再次尝试'
          })
          break
          case 'Z1009':
          this.$Notice.info({
              title: '数据为空',
              desc: '数据为空,请新建笔记'
          })
          break
          default:
          break
        }
      })
    },
    show (params) {
      this.$router.push({
        name:'笔记详情',
        query:{
          params : params['row']
        }
      })
    },
    restore (params) {
      let flag = {
        'type': 'trash',
        'data': '0'
      }
      let data = {
        'notename': AES.Encrypt(params.row.notename),
        'flag': flag,
        'token': this.token.trim()
      }
      data = JSON.stringify(data)
      let req_params = {'data': AES.Encrypt(data)}
      http.post('/api/setflag', req_params).then((res) => {
        res.data = eval('(' + res.data + ')')
        switch(res.data.code ){
          case'Z1000':
          this.reload()
          break
          case 'Z1001':
          this.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
          })
          break
          case 'Z1002':
          this.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
          })
          break
          case 'Z1004':
          this.$Notice.error({
              title: '请求失败',
              desc: '认证失败,请稍后再次尝试'
          })
          break
          default:
          break
        }
      })
    },
    delete (params) {
      let flag = {
        'type': 'trash',
        'data': '-1'
      }
      let data = {
        'notename': AES.Encrypt(params.row.notename),
        'flag': flag,
        'token': this.token.trim()
      }
      data = JSON.stringify(data)
      let req_params = {'data': AES.Encrypt(data)}
      http.post('/api/setflag', req_params).then((res) => {
        res.data = eval('(' + res.data + ')')
        switch(res.data.code ){
          case'Z1000':
          this.reload()
          break
          case 'Z1001':
          this.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
          })
          break
          case 'Z1002':
          this.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
          })
          break
          case 'Z1004':
          this.$Notice.error({
              title: '请求失败',
              desc: '认证失败,请稍后再次尝试'
          })
          break
          default:
          break
        }
      })
    },
    handlePage (pageNum) {
      this.page.pageNum = pageNum
      this.getTableData()
    },
    handlePageSize (pageSize) {
      this.page.pageSize = pageSize
      this.getTableData()
    }
  },
  mounted () {
    this.getTableData()
  },
}
