import {isemail, isusername, ischecknum, ispassword} from '@/libs/validate'
import AES  from '@/libs/crypto'
import http  from '@/libs/http'
export default {
  name: 'RegisterForm',
  props: {
    usernameRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: isusername, min:1},
        ]
      }
    },
    emailRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: isemail }
        ]
      }
    },
    checknumRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: ischecknum }
        ]
      }
    },
    passwordRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: ispassword }
        ]
      }
    },
    passwordconfirmRules: {
      type: Array,
      default: () => {
        return [
          { required: true, trigger: 'blur', validator: ispassword }
        ]
      }
    }
  },
  data () {
    return {
      capta: '',
      form: {
        username: '',
        email: '',
        checknum: '',
        password: '',
        passwordconfirm: ''
      },
      username: {
        'type': 'username', 
        'data': ''
      }
    }
  },
  computed: {
    rules () {
      return {
        username: this.usernameRules,
        email: this.emailRules,
        checknum: this.checknumRules,
        password: this.passwordRules,
        passwordconfirm: this.passwordconfirmRules
      }
    }
  },
  methods: {
    handleusername () {
      this.$refs.RegisterForm.validateField('username', (valid) => {

      })
   },

   handlemail () {
      this.$refs.RegisterForm.validateField('email', (valid) => {
      })
   },

    getchecknum () {
      if (this.form.email== ''){
        this.$refs.RegisterForm.validateField('email', (valid) => {
        })
      }
      else{
        let data = {
          'type': 'email', 
          'data': this.form.email
        }
        data = JSON.stringify(data)
        let params = {'data': AES.Encrypt(data)}
        http.post('/api/getchecknum', params).then((res) => {
          res.data = eval('(' + res.data + ')')
          switch(res.data.code ){
            case'Z1000':
            this.$Notice.success({
                title: '发送邮件成功',
                desc: '请打开邮件查收验证码 '
            })
            this.capta = res.data.data.capta
            break
            case 'Z1001':
            this.$Notice.error({
                title: '发送邮件失败',
                desc: '系统发生异常,请稍后再次尝试'
            })
            break
            case 'Z1002':
            this.$Notice.error({
                title: '发送邮件失败',
                desc: '系统发生异常,请稍后再次尝试'
            })
            break
          }
        })
      }
    },
    handleSubmit () {
     this.$refs.RegisterForm.validate( (valid) => {
        if (valid) {
          if (this.form.password != this.form.passwordconfirm){
            this.$Notice.error({
                title: '二次密码输入错误',
                desc: '二次密码输入错误,请重新输入!'
            })
          }
          else{
            this.$emit('on-success-valid', {
              username: this.form.username,
              email: this.form.email,
              password: this.form.password,
              checknum: this.form.checknum,
              capta: this.capta
            })
          }
        }
      })
    },

    ToLogin () {
      setTimeout(() => {
        this.$router.push({
        path: '/login'
        })
      },1000)
    }
  }  
}
