import RSA  from '@/libs/crypto'
import http  from '@/libs/http'
import {getToken } from '@/libs/util'
export default {
  inject: ['reload'],
  name: 'tables_page',
  data () {
    return {
      target: '',
      min: 1,
      max: 65535,
      scanner: 'nmap',
      rate: 1000,
    }
  },

  created () {
    this.$nextTick(this.getParams())
  },

  methods: {
    getParams () {
      this.target = this.$route.query.params
    },

    handleSubmit (params) {
      let data = {
        'target': this.target,
        'token': getToken(),
        'scanner': this.scanner,
        'min_port': this.min + '',
        'max_port': this.max + '',
        'rate': this.rate + ''
      }
      data = JSON.stringify(data)
      let req_params = {'data': RSA.Encrypt(data)}
      http.post('/api/scan_set', req_params).then((res) => {
        res.data = eval('(' + res.data + ')')
        switch(res.data.code ){
          case'Z1000':
          this.$Notice.success({
              title: '已设置扫描',
              desc: '请稍后在目标列表中开始扫描'
          })
          setTimeout(() => {
            this.$router.push({
                path: '/target/list'
              })
            },5000)
          break
          case 'Z1001':
          this.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
          })
          break
          case 'Z1002':
          this.$Notice.error({
              title: '请求失败',
              desc: '系统发生异常,请稍后再次尝试'
          })
          break
          case 'Z1004':
          this.$Notice.error({
              title: '请求失败',
              desc: '认证失败,请稍后再次尝试'
          })
          break
          default:
          break
        }
      })
    }
  }
}
