import http from '@/libs/http'
import AES from '@/libs/crypto'
import {getToken } from '@/libs/util'
export default {
  inject: ['reload'],
  name: 'MarkdownEditor',
  data () {
    return {
      new_notename: '',
      new_content: '',
      old_notename: '',
      old_content: '',
      flag : '',
      token: getToken(),
      publish_flag: '0',
    }
  },
  created () {
    this.nextTick(this.getParams())
  },
  methods: {
    getParams () {
      if (this.$route.query.params.notename){
        this.new_notename = this.$route.query.params.notename
        this.new_content = this.$route.query.params.content
        this.old_notename = this.$route.query.params.notename
        this.old_content = this.$route.query.params.content
      }    
    },
    handlnotename () {
      if (this.new_notename == ""){
        this.$Notice.error({
                  title: '笔记名字不能为空',
                  desc: '请输入笔记名字 '
          })
      }
      else{
        let data = {
            'type': 'notename', 
            'token': this.token,
            'data': AES.Encrypt(this.new_notename)
        }
        data = JSON.stringify(data)
        let params = {'data': AES.Encrypt(data)}
        http.post('/api/query', params).then((res) => {
            res.data = eval('(' + res.data + ')')
            switch(res.data.code ){
                case 'Z1001':
                this.$Notice.error({
                  title: '系统异常',
                  desc: '系统发生异常,请稍后再次尝试'
                })
                break
                case 'Z1002':
                this.$Notice.error({
                  title: '请求异常',
                  desc: '请求异常,请稍后再次尝试'
                })
                break
                case 'Z1004':
                this.$Notice.error({
                  title: '修改失败',
                  desc: '认证失败,请稍后再次尝试'
                })
                break
                case 'Z10010':
                if (this.new_notename != this.old_notename){
                  this.flag = '1'
                  this.$Notice.error({
                    title: '笔记已存在',
                    desc: '笔记名字已存在,请重新输入笔记名字'
                  })
                }
                break
                default:
                break
            }
        })
      }
    },
    change (status) {
      if (status){
        this.publish_flag = '1'
        console.log(this.publish_flag)
      }

      else{
        this.publish_flag = '0'
        console.log(this.publish_flag)
      }
    },
    handleSubmit () {
      if (this.new_notename == ""){
        this.$Notice.error({
          title: '笔记名字不能为空',
          desc: '请输入笔记名字 '
          })
      }
      else{
        if (this.flag == "1"){
          this.$Notice.error({
            title: '笔记已存在',
            desc: '笔记名字已存在,请重新输入笔记名字'
          })
        }
        else{
          if (this.new_content == ""){
            this.$Notice.error({
                    title: '笔记内容不能为空',
                    desc: '请输入笔记内容'
            })
          }
          else{
            let data = {
              'token': this.token,
              'old_notename': AES.Encrypt(this.old_notename),
              'old_content': AES.Encrypt(this.old_content),
              'new_notename': AES.Encrypt(this.new_notename),
              'new_content': AES.Encrypt(this.new_content),
              'publish_flag': this.publish_flag,
              'trash_flag': '0',
            }
            data = JSON.stringify(data)
            let params = {'data': AES.Encrypt(data)}
            http.post('/api/editnote', params).then((res) => {
                res.data = eval('(' + res.data + ')')
                switch(res.data.code ){
                    case 'Z1000':
                    this.$Notice.success({
                      title: '修改成功',
                      desc: '笔记修改成功,请到笔记列表查看'
                    })
                    setTimeout(() => {
                      this.$router.push({
                      path: '/components/notelist'
                      })
                    },1000)
                    break
                    case 'Z1001':
                    this.$Notice.error({
                      title: '系统异常',
                      desc: '系统发生异常,请稍后再次尝试'
                    })
                    break
                    case 'Z1002':
                    this.$Notice.error({
                      title: '系统异常',
                      desc: '系统发生异常,请稍后再次尝试'
                    })
                    break
                    case 'Z1004':
                    this.$Notice.error({
                      title: '修改失败',
                      desc: '认证失败,请稍后再次尝试'
                    })
                    break
                    default:
                    break
                }
            })
          }
        }
      }
    },
  }
}
